mapper.update:
	
		update board 
		set order_no = order_no + 1
		where group_no = #{groupNo} and order_no >=  #{orderNo}

mapper.select:

select no, title, content, member_no as memberNo, group_no as groupNo, order_no as orderNo, depth 
from board 
where no=#{no}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT (*) OVER () AS TOTCNT, COUNT (*) OVER () - ROWNUM +1 as articleSequence FROM ( SELECT a.NO as no, a.TITLE as title, a.CONTENT as content, a.MEMBER_NO as memberNo, b.NAME as name, a.VIEW_CNT as viewCount, a.REG_DATE as regDate, a.ORDER_NO as orderNo, a.GROUP_NO as groupNo, a.DEPTH as depth FROM board a, MEMBER b WHERE a.member_no = b.no
AND (a.TITLE like #{word} OR a.content like #{word} OR b.name like #{word} )
ORDER BY a.group_no DESC, a.order_no asc) A) WHERE RNUM > #{start} AND RNUM <= #{end}


	SELECT NO, FILE_NAME as fileName, ORIGIN_FILE_NAME as originFileName, MINE_TYPE as mineType 
	from UPLOAD_FILE
	WHERE BOARD_NO = #{no}
